import ExpenseItem from "./ExpenseItem";
import { AppContext } from "../../context/AppContext";
import { useContext, useEffect } from "react";
import { Expense } from "../../types/types";
import { fetchExpenses } from "../../utils/expense-utils";

const ExpenseList = () => {
  const { expenses, setExpenses } = useContext(AppContext);

  // Function to load expenses and handle errors
  const loadExpenses = async () => {
    try {
      const expenseList = await fetchExpenses(); // Fetch expenses from the server
      setExpenses(expenseList); // Update context with fetched expenses
    } catch (err: any) {
      console.error("Failed to load expenses:", err.message);
    }
  };

  // Fetch expenses on component mount
  useEffect(() => {
    loadExpenses();
  }, [setExpenses]); // Dependency array includes setExpenses to prevent stale closures

  return (
    <ul className="list-group">
      {expenses.map((expense: Expense) => (
        <ExpenseItem 
          id={expense.id} 
          description={expense.description} 
          cost={expense.cost} 
          key={expense.id} 
        />
      ))}
    </ul>
  );
};

export default ExpenseList;
